mapper.update:
UPDATE tbl_board
SET view_count = view_count + 1
WHERE bno = #{bno}

UPDATE tbl_board
SET report_count = report_count + 1
WHERE bno = #{bno}

UPDATE tbl_board
SET like_count = like_count + #{number}
WHERE bno = #{bno}

mapper.select:
SELECT * FROM tbl_board
WHERE writer = #{account}
ORDER BY bno DESC
LIMIT #{page.pageStart}, #{page.amount}
