mapper.select:
SELECT COUNT(*)
FROM tbl_user

    WHERE account_number = #{keyword}


    WHERE email = #{keyword}

mapper.update:
UPDATE tbl_user
SET report = report + 1
WHERE account_number = #{accountNumber}

UPDATE tbl_user
SET session_id = #{sessionId}, limit_time = #{limitTime}
WHERE account_number = #{account}

mapper.select:
SELECT follower
FROM tbl_follow
WHERE following = #{account}
LIMIT 0, #{number}

mapper.delete:
DELETE FROM tbl_follow
WHERE following = #{me}
AND follower = #{you}
